import React from 'react';
import {
    View,
    Text,
    Button,
    RefreshControl
} from 'react-native';


export default function Alert({error, refresh}){
    // alert that refreshes
    // after button press
    return (
        <View>
            <Text>{error}</Text>
            <Button onPress={refresh} title="OK"/>
        </View>
    );
};
